SELECT t3.mt4Account 'mt4Account', t3.rebate_currency, t3.dataSourceId, t3.commissionsIN 'commissionsIn', t3.commissionsOUT 'commissionsOut' FROM ( SELECT t2.mt4Account, v2.rebate_currency, v2.dataSourceId, SUM( CASE WHEN t2.commission_flag = 0 THEN t2.pip_commission ELSE 0 END ) AS 'commissionsIN', SUM( CASE WHEN t2.commission_flag = 1 THEN t2.pip_commission ELSE 0 END ) AS 'commissionsOUT' FROM tb_commission_ib t2 left join tb_commission_ib_volume v2 on t2.ticket = v2.ticket and t2.server=v2.dataSourceId WHERE t2.close_time > ? AND t2.close_time <= ? GROUP BY t2.mt4Account ORDER BY t2.mt4Account ) AS t3, ( SELECT t1.mt4Account, SUM(t1.volume) AS 'volumes' FROM ( SELECT DISTINCT t.ticket, t.mt4Account, v.volume FROM tb_commission_ib t left join tb_commission_ib_volume v on t.ticket = v.ticket and t.server = v.dataSourceId WHERE t.close_time > ? AND t.close_time <= ? ) AS t1 GROUP BY t1.mt4Account ORDER BY t1.mt4Account ) AS t4 WHERE t3.mt4Account = t4.mt4Account;;
